import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { 
  Cog, 
  Beaker, 
  Fuel, 
  Wheat, 
  Utensils, 
  Monitor, 
  Zap,
  Users,
  BookOpen,
  ArrowRight
} from 'lucide-react';

// Import department logos
import agricLogo from '../logo/Agric.png';
import chemLogo from '../logo/chem.png';
import comLogo from '../logo/com.png';
import electLogo from '../logo/elect.png';
import foodLogo from '../logo/food.png';
import imLogo from '../logo/im.png';

const DepartmentsPage = () => {
  const departments = [
    {
      id: 'mechanical',
      name: 'Mechanical Engineering',
      logo: imLogo,
      description: 'Design, analysis, and manufacturing of mechanical systems and machines.',
      hod: 'Prof. John Akpan',
      studentCount: '450+',
      courses: '25+',
      color: 'from-blue-500 to-blue-600',
      bgImage: 'bg-gradient-to-br from-blue-50 to-blue-100'
    },
    {
      id: 'chemical',
      name: 'Chemical Engineering',
      icon: Beaker,
      logo: chemLogo,
      description: 'Process design, optimization, and control in chemical industries.',
      hod: 'Dr. Mary Okon',
      studentCount: '320+',
      courses: '22+',
      color: 'from-green-500 to-green-600',
      bgImage: 'bg-gradient-to-br from-green-50 to-green-100'
    },
    {
      id: 'petroleum',
      name: 'Petroleum Engineering',
      icon: Fuel,
      logo: null,
      description: 'Exploration, extraction, and production of oil and gas resources.',
      hod: 'Prof. David Etim',
      studentCount: '280+',
      courses: '20+',
      color: 'from-orange-500 to-orange-600',
      bgImage: 'bg-gradient-to-br from-orange-50 to-orange-100'
    },
    {
      id: 'agricultural',
      name: 'Agricultural Engineering',
      icon: Wheat,
      logo: agricLogo,
      description: 'Application of engineering principles to agricultural production.',
      hod: 'Dr. Grace Udoh',
      studentCount: '200+',
      courses: '18+',
      color: 'from-yellow-500 to-yellow-600',
      bgImage: 'bg-gradient-to-br from-yellow-50 to-yellow-100'
    },
    {
      id: 'food',
      name: 'Food Engineering',
      icon: Utensils,
      logo: foodLogo,
      description: 'Processing, preservation, and packaging of food products.',
      hod: 'Prof. Samuel Udo',
      studentCount: '180+',
      courses: '16+',
      color: 'from-red-500 to-red-600',
      bgImage: 'bg-gradient-to-br from-red-50 to-red-100'
    },
    {
      id: 'computer',
      name: 'Computer Engineering',
      icon: Monitor,
      logo: comLogo,
      description: 'Hardware and software systems design and development.',
      hod: 'Dr. Emmanuel Bassey',
      studentCount: '380+',
      courses: '24+',
      color: 'from-purple-500 to-purple-600',
      bgImage: 'bg-gradient-to-br from-purple-50 to-purple-100'
    },
    {
      id: 'electrical',
      name: 'Electrical Engineering',
      icon: Zap,
      logo: electLogo,
      description: 'Power systems, electronics, and electrical infrastructure.',
      hod: 'Prof. Peter Obot',
      studentCount: '420+',
      courses: '26+',
      color: 'from-indigo-500 to-indigo-600',
      bgImage: 'bg-gradient-to-br from-indigo-50 to-indigo-100'
    }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const cardVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.6,
        ease: "easeOut"
      }
    }
  };

  return (
    <div className="min-h-screen pt-20">
      {/* Header Section */}
      <section className="section-padding bg-gradient-to-r from-nuesa-green to-nuesa-green-light text-white">
        <div className="container-custom text-center">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <h1 className="text-4xl md:text-5xl font-bold mb-6">
              Engineering Departments
            </h1>
            <p className="text-xl md:text-2xl mb-8 max-w-3xl mx-auto">
              Explore our seven world-class engineering departments and discover 
              the resources that will shape your academic journey.
            </p>
            <div className="flex justify-center items-center space-x-8 text-lg">
              <div className="flex items-center space-x-2">
                <Users className="w-6 h-6" />
                <span>2,000+ Students</span>
              </div>
              <div className="flex items-center space-x-2">
                <BookOpen className="w-6 h-6" />
                <span>150+ Courses</span>
              </div>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Departments Grid */}
