import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { motion } from 'framer-motion';
import { 
  ArrowLeft, 
  User, 
  Users, 
  BookOpen, 
  Download,
  Calendar,
  Clock,
  FileText
} from 'lucide-react';

const DepartmentDetail = () => {
  const { departmentId } = useParams();

  // Sample data - in real app, this would come from an API
  const departmentData = {
    mechanical: {
      name: 'Mechanical Engineering',
      hod: 'Prof. John Akpan',
      description: 'The Mechanical Engineering Department is dedicated to advancing the field through innovative research, comprehensive education, and practical applications in design, manufacturing, and analysis of mechanical systems.',
      vision: 'To be a leading department in mechanical engineering education and research in West Africa.',
      mission: 'To provide quality education and conduct cutting-edge research in mechanical engineering while serving the community.',
      stats: {
        students: '450+',
        faculty: '12',
        courses: '25+',
        labs: '8'
      }
    },
    // Add other departments as needed
  };


    const levels = [
      {
        level: '100',
        name: 'First Year',
        semesters: [
          {
            semester: '1st Semester',
            courses: [
              { code: 'MEE 101', title: 'Introduction to Engineering', credits: 3, description: 'Overview of engineering disciplines; engineering ethics; problem solving.' },
              { code: 'MTH 101', title: 'Mathematics I', credits: 4, description: 'Calculus I: limits, derivatives, applications.' },
              { code: 'PHY 101', title: 'Physics I', credits: 3, description: 'Mechanics, motion, and basic dynamics.' },
              { code: 'CHM 101', title: 'Chemistry I', credits: 3, description: 'Structure of atoms, periodic table, bonding.' },
              { code: 'GST 101', title: 'Use of English I', credits: 2, description: 'English composition and communication skills.' }
            ]
          },
          {
            semester: '2nd Semester',
            courses: [
              { code: 'MEE 102', title: 'Engineering Drawing', credits: 3, description: 'Technical drawing, orthographic projection, CAD basics.' },
              { code: 'MTH 102', title: 'Mathematics II', credits: 4, description: 'Vector calculus, ordinary differential equations.' },
              { code: 'PHY 102', title: 'Physics II', credits: 3, description: 'Electricity, magnetism, and waves.' },
              { code: 'CHM 102', title: 'Chemistry II', credits: 3, description: 'Organic chemistry, introduction to chemical reactions.' },
              { code: 'GST 102', title: 'Use of English II', credits: 2, description: 'Advanced communication, report writing.' }
            ]
          }
        ]
      },
      {
        level: '200',
        name: 'Second Year',
        semesters: [
          {
            semester: '1st Semester',
            courses: [
              { code: 'MEE 201', title: 'Engineering Materials', credits: 3, description: 'Properties of engineering materials: metals, polymers, ceramics.' },
              { code: 'MEE 203', title: 'Engineering Mechanics I', credits: 3, description: 'Statics: force systems, equilibrium.' },
              { code: 'MTH 201', title: 'Mathematics III', credits: 4, description: 'Linear algebra, multivariable calculus.' },
              { code: 'GST 201', title: 'Fundamentals of Management', credits: 2, description: 'Management principles, organizational behaviour.' },
              { code: 'PHY 201', title: 'Modern Physics', credits: 3, description: 'Relativity, quantum theory basics.' }
            ]
          },
          {
            semester: '2nd Semester',
            courses: [
              { code: 'MEE 202', title: 'Engineering Thermodynamics I', credits: 3, description: 'First and second laws, heat engines.' },
              { code: 'MEE 204', title: 'Engineering Mechanics II', credits: 3, description: 'Dynamics: kinematics, kinetics, energy methods.' },
              { code: 'MTH 202', title: 'Probability & Statistics for Engineers', credits: 3, description: 'Statistical methods, probability, sampling.' },
              { code: 'GST 202', title: 'Entrepreneurship Skills', credits: 2, description: 'Idea generation, business plan basics.' },
              { code: 'MEE 206', title: 'Fluid Mechanics I', credits: 3, description: 'Fluid properties, fluid statics, control volume analysis.' }
            ]
          }
        ]
      },
      {
        level: '300',
        name: 'Third Year',
        semesters: [
          {
            semester: '1st Semester',
            courses: [
              { code: 'MEE 301', title: 'Machine Design I', credits: 3, description: 'Strength of materials, stress, strain, design of simple machines.' },
              { code: 'MEE 303', title: 'Heat Transfer', credits: 3, description: 'Conduction, convection, and radiation heat transfer.' },
              { code: 'MEE 305', title: 'Manufacturing Processes', credits: 3, description: 'Casting, forming, machining etc.' },
              { code: 'MTH 301', title: 'Numerical Methods', credits: 3, description: 'Computational techniques for engineering problems.' },
              { code: 'ELE 301', title: 'Electrical Machines & Drives', credits: 3, description: 'Fundamentals of motors, generators, and control.' }
            ]
          },
          {
            semester: '2nd Semester',
            courses: [
              { code: 'MEE 302', title: 'Theory of Machines I', credits: 3, description: 'Kinematics of machines, mechanisms.' },
              { code: 'MEE 304', title: 'Engineering Measurement & Instrumentation', credits: 3, description: 'Sensors, transducers, measurement techniques.' },
              { code: 'MEE 306', title: 'Fluid Mechanics II', credits: 3, description: 'Flow in pipes, pumps, turbines.' },
              { code: 'MEE 308', title: 'Thermodynamics II', credits: 3, description: 'Power cycles, refrigeration, compressors.' },
              { code: 'GST 301', title: 'Industrial Safety & Hazard Management', credits: 2, description: 'Principles of safety, risk assessment.' }
            ]
          }
        ]
      },
      {
        level: '400',
        name: 'Fourth Year',
        semesters: [
          {
            semester: '1st Semester',
            courses: [
              { code: 'MEE 401', title: 'Machine Design II', credits: 3, description: 'Design of complex machine elements, shafts, gears, bearings.' },
              { code: 'MEE 403', title: 'Dynamics of Machines', credits: 3, description: 'Vibration, balancing, dynamic forces.' },
              { code: 'MEE 405', title: 'Internal Combustion Engines', credits: 3, description: 'Engine cycles, performance, emissions.' },
              { code: 'MEE 407', title: 'Control Engineering', credits: 3, description: 'Feedback control systems, stability, applications.' },
              { code: 'MEE 409', title: 'CAD/CAM', credits: 3, description: 'Computer‚Äêaided design, manufacturing automation.' }
            ]
          },
          {
            semester: '2nd Semester',
            courses: [
              { code: 'MEE 402', title: 'Mechanical Vibrations', credits: 3, description: 'Free and forced vibrations, damping.' },
              { code: 'MEE 404', title: 'Refrigeration & Air Conditioning', credits: 3, description: 'Thermal comfort, HVAC systems.' },
              { code: 'MEE 406', title: 'Power Plant Engineering', credits: 3, description: 'Steam, gas, hydro power systems.' },
              { code: 'MEE 408', title: 'Manufacturing System & Production Engineering', credits: 3, description: 'Production planning, quality control, lean manufacturing.' },
              { code: 'GST 401', title: 'Project Management', credits: 2, description: 'Planning, resource, cost and time management.' }
            ]
          }
        ]
      },
      {
        level: '500',
        name: 'Fifth Year',
        semesters: [
          {
            semester: '1st Semester',
            courses: [
              { code: 'MEE 501', title: 'Advanced Machine Design', credits: 3, description: 'Finite element methods, fatigue, failure analysis.' },
              { code: 'MEE 503', title: 'Advanced Thermodynamics', credits: 3, description: 'Combustion, gas dynamics.' },
              { code: 'MEE 505', title: 'Heat Exchangers & Solar Thermal Systems', credits: 3, description: 'Design of heat exchangers; solar energy applications.' },
              { code: 'MEE 507', title: 'Mechanical System Optimization', credits: 3, description: 'Optimization techniques, reliability engineering.' },
              { code: 'MEE 509', title: 'Environmental & Energy Engineering', credits: 2, description: 'Renewable energy, sustainability.' }
            ]
          },
          {
            semester: '2nd Semester',
            courses: [
              { code: 'MEE 502', title: 'Engineering Project/Thesis', credits: 6, description: 'Capstone design or research project.' },
              { code: 'MEE 504', title: 'Industrial Training', credits: 2, description: 'Practical exposure in industry.' },
              { code: 'MEE 506', title: 'Maintenance Engineering', credits: 3, description: 'Maintenance planning, reliability, asset management.' },
              { code: 'MEE 508', title: 'Fluid Power Engineering', credits: 3, description: 'Hydraulic and pneumatic systems design and applications.' },
              { code: 'MTH 502', title: 'Advanced Computational Methods', credits: 2, description: 'Simulation, modeling, computational tools.' }
            ]
          }
        ]
      }
    ];


  const department = departmentData[departmentId] || departmentData.mechanical;

  return (
    <div className="min-h-screen pt-20">
      {/* Header Section */}
      <section className="section-padding bg-gradient-to-r from-nuesa-green to-nuesa-green-light text-white">
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <Link 
              to="/departments" 
              className="inline-flex items-center space-x-2 text-white/80 hover:text-white mb-6 transition-colors"
            >
              <ArrowLeft className="w-5 h-5" />
              <span>Back to Departments</span>
            </Link>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 items-center">
              <div className="lg:col-span-2">
                <h1 className="text-4xl md:text-5xl font-bold mb-4">
                  {department.name}
                </h1>
                <p className="text-xl mb-6 leading-relaxed">
                  {department.description}
                </p>
                <div className="flex items-center space-x-2 text-lg">
                  <User className="w-5 h-5" />
                  <span>Head of Department: <strong>{department.hod}</strong></span>
                </div>
              </div>

              <div className="bg-white/10 backdrop-blur-sm rounded-xl p-6">
                <h3 className="text-xl font-semibold mb-4">Quick Stats</h3>
                <div className="space-y-3">
                  <div className="flex justify-between">
                    <span>Students:</span>
                    <span className="font-bold">{department.stats.students}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Faculty:</span>
                    <span className="font-bold">{department.stats.faculty}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Courses:</span>
                    <span className="font-bold">{department.stats.courses}</span>
                  </div>
                  <div className="flex justify-between">
                    <span>Labs:</span>
                    <span className="font-bold">{department.stats.labs}</span>
                  </div>
                </div>
              </div>
            </div>
          </motion.div>
        </div>
      </section>

      {/* Vision & Mission */}
      <section className="section-padding bg-white">
        <div className="container-custom">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <motion.div
              initial={{ opacity: 0, x: -30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
              className="card p-8"
            >
              <h3 className="text-2xl font-bold text-nuesa-green mb-4">Our Vision</h3>
              <p className="text-gray-700 leading-relaxed">{department.vision}</p>
            </motion.div>

            <motion.div
              initial={{ opacity: 0, x: 30 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
              className="card p-8"
            >
              <h3 className="text-2xl font-bold text-nuesa-green mb-4">Our Mission</h3>
              <p className="text-gray-700 leading-relaxed">{department.mission}</p>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Courses by Level */}
      <section className="section-padding bg-gray-50">
        <div className="container-custom">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="text-center mb-12"
          >
            <h2 className="text-3xl font-bold text-gray-900 mb-4">
              Course Materials by Level
            </h2>
            <p className="text-xl text-gray-600">
              Access course materials organized by academic level and semester
            </p>
          </motion.div>

          <div className="space-y-12">
            {levels.map((level, levelIndex) => (
              <motion.div
                key={level.level}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: levelIndex * 0.1 }}
                viewport={{ once: true }}
                className="bg-white rounded-xl shadow-lg overflow-hidden"
              >
                <div className="bg-nuesa-green text-white p-6">
                  <h3 className="text-2xl font-bold">
                    {level.level} Level - {level.name}
                  </h3>
                </div>

                <div className="p-6">
                  {level.semesters.map((semester, semesterIndex) => (
                    <div key={semesterIndex} className="mb-8 last:mb-0">
                      <h4 className="text-xl font-semibold text-gray-900 mb-6 flex items-center">
                        <Calendar className="w-5 h-5 mr-2 text-nuesa-orange" />
                        {semester.semester}
                      </h4>

                      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {semester.courses.map((course, courseIndex) => (
                          <motion.div
                            key={courseIndex}
                            whileHover={{ scale: 1.02 }}
                            className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-all duration-300"
                          >
                            <div className="flex justify-between items-start mb-2">
                              <h5 className="font-bold text-nuesa-green">{course.code}</h5>
                              <span className="text-sm text-gray-500 flex items-center">
                                <Clock className="w-3 h-3 mr-1" />
                                {course.credits} credits
                              </span>
                            </div>

                            <h6 className="font-semibold text-gray-900 mb-2">{course.title}</h6>
                            <p className="text-sm text-gray-600 mb-4">{course.description}</p>

                            <Link
                              to={`/departments/${departmentId}/course/${course.code.toLowerCase().replace(' ', '-')}`}
                              className="inline-flex items-center space-x-2 text-nuesa-green hover:text-nuesa-green-dark transition-colors"
                            >
                              <FileText className="w-4 h-4" />
                              <span className="text-sm font-medium">View Materials</span>
                            </Link>
                          </motion.div>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Sidebar Ad Space */}
      <section className="section-padding bg-white">
        <div className="container-custom">
          <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
            <div className="lg:col-span-3">
              <h3 className="text-2xl font-bold text-gray-900 mb-6">Additional Resources</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="card p-6">
                  <BookOpen className="w-8 h-8 text-nuesa-green mb-4" />
                  <h4 className="font-semibold mb-2">Course Handbooks</h4>
                  <p className="text-gray-600 text-sm">Access comprehensive course handbooks and syllabi.</p>
                </div>
                <div className="card p-6">
                  <Users className="w-8 h-8 text-nuesa-green mb-4" />
                  <h4 className="font-semibold mb-2">Study Groups</h4>
                  <p className="text-gray-600 text-sm">Join or create study groups with your classmates.</p>
                </div>
              </div>
            </div>

            {/* Ad Space */}
            <div className="lg:col-span-1">
              <div className="sticky top-24">
                <div className="bg-gray-100 rounded-lg p-6 h-96 flex items-center justify-center border-2 border-dashed border-gray-300">
                  <div className="text-center text-gray-500">
                    <div className="w-16 h-16 bg-gray-300 rounded-lg mx-auto mb-4 flex items-center justify-center">
                      <span className="text-sm">AD</span>
                    </div>
                    <p className="text-sm">Google AdSense</p>
                    <p className="text-xs">300x250</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default DepartmentDetail;
