{departments.map((department) => {
  const Icon = department.icon;

  return (
    <motion.div
      key={department.id}
      variants={cardVariants}
      whileHover={{ 
        scale: 1.05,
        transition: { duration: 0.3 }
      }}
      className="group"
    >
      <Link to={`/departments/${department.id}`}>
        <div className={`card overflow-hidden h-full ${department.bgImage}`}>
          {/* Department Logo & Header */}
          <div className="p-6 pb-4">
            <div className="flex items-center mb-4">
              {/* Department Logo */}
              {department.logo && (
                <div className="w-16 h-16 mr-4 flex-shrink-0">
                  <img
                    src={department.logo}
                    alt={`${department.name} logo`}
                    className="w-full h-full object-contain rounded-lg"
                  />
                </div>
              )}

              {/* Department Icon */}
              <div className={`w-12 h-12 rounded-full bg-gradient-to-r ${department.color} flex items-center justify-center group-hover:scale-110 transition-transform duration-300`}>
                <Icon className="w-6 h-6 text-white" />
              </div>
            </div>

            <h3 className="text-xl font-bold text-gray-900 mb-2 group-hover:text-nuesa-green transition-colors">
              {department.name}
            </h3>

            <p className="text-gray-600 text-sm leading-relaxed mb-4">
              {department.description}
            </p>
          </div>

          {/* Department Stats */}
          <div className="px-6 pb-4">
            <div className="grid grid-cols-2 gap-4 mb-4">
              <div className="text-center">
                <div className="text-lg font-bold text-nuesa-green">{department.studentCount}</div>
                <div className="text-xs text-gray-600">Students</div>
              </div>
              <div className="text-center">
                <div className="text-lg font-bold text-nuesa-green">{department.courses}</div>
                <div className="text-xs text-gray-600">Courses</div>
              </div>
            </div>

            <div className="text-center mb-4">
              <div className="text-sm text-gray-700">
                <span className="font-semibold">HOD:</span> {department.hod}
              </div>
            </div>
          </div>

          {/* Action Button */}
          <div className="px-6 pb-6">
            <div className="bg-white rounded-lg p-3 flex items-center justify-between group-hover:bg-nuesa-green group-hover:text-white transition-all duration-300">
              <span className="font-semibold">View Materials</span>
              <ArrowRight className="w-5 h-5 group-hover:translate-x-1 transition-transform duration-300" />
            </div>
          </div>
        </div>
      </Link>
    </motion.div>
  );
})}
